css:
.ColorPicker-loader {
  background: rgba(0, 0, 0, 0.4);
  width: 100%;
  height: 100px;
  font-size: 0;
  text-align: center;
  pointer-events: none;
  position: absolute;
  top: 0;
  z-index: 40;
  opacity: 0;
  -webkit-transition: opacity .3s;
}
.is--searching .ColorPicker-loader {
  opacity: 1;
}
.is--error .ColorPicker-loader {
  opacity: 0;
}
.is--searching .ColorPicker-loaderDot {
  background: #fff;
  width: 14px;
  height: 14px;
  border-radius: 100%;
  margin: -7px 4px 0;
  position: relative;
  top: 50%;
  display: inline-block;
  -webkit-transform-style: preserve-3d;
  -webkit-backface-visibility: hidden;
  -webkit-animation: inflate alternate infinite 0.7s ease-in-out;
  -webkit-animation-fill-mode: both;
  -webkit-animation-play-state: running;
}
.is--searching .ColorPicker-loaderDot:nth-child(2) {
  -webkit-animation-delay: .1s;
}
.is--searching .ColorPicker-loaderDot:nth-child(3) {
  -webkit-animation-delay: .2s;
}
@-webkit-keyframes inflate {
  0%,
  40% {
    -webkit-transform: scale(0);
  }
  100% {
    -webkit-transform: scale(1);
  }
}
