css:
.ColorPicker {
  height: 0;
  position: absolute;
  visibility: hidden;
  opacity: 0;
  pointer-events: none;
  user-select: none;
  z-index: 100;
  transition: opacity             .15s,
            visibility        0 .15s;
  backface-visibility: hidden;
  transform-style: preserve-3d;
}
.ColorPicker.is--open {
  visibility: visible;
  opacity: 1;
  pointer-events: all;
  transition: opacity .037s;
}
.ColorPicker-arrow {
  width: 20px;
  height: 10px;
  border-top-color: #ff0000;
  border-bottom-color: #ff0000;
  position: absolute;
  top: 0;
  bottom: 0;
}
.ColorPicker-arrow:before,
.ColorPicker-arrow:after {
  content: '';
  width: 0;
  height: 0;
  border-left: 10px solid transparent;
  border-right: 10px solid transparent;
  border-bottom: 10px solid transparent;
  position: absolute;
  transform: translate(-50%, 0);
}
.ColorPicker-arrow:before {
  border-bottom-color: #fff;
  opacity: .89;
}
.ColorPicker-arrow:after {
  border-bottom-color: #ff0000;
  border-bottom-color: inherit;
}
.is--flipped .ColorPicker-arrow {
  top: auto;
  bottom: 0;
}
.is--flipped .ColorPicker-arrow:before {
  border-bottom: none;
  border-top: 10px solid #ffffff;
}
.is--flipped .ColorPicker-arrow:after {
  border-bottom: none;
  border-top: 10px solid #ff0000;
  border-top-color: inherit;
}
.ColorPicker-color {
  background: red;
  width: 100%;
  height: 90px;
  cursor: pointer;
  margin-top: 10px;
  border-radius: 2px 2px 0 0;
  position: absolute;
  top: 0;
  bottom: auto;
}
.ColorPicker-color:before,
.ColorPicker-color:after {
  content: '';
  border-radius: 2px 2px 0 0;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: -1;
}
.ColorPicker-color:before {
  background: #fff;
  opacity: .92;
}
.ColorPicker-color:after {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAJElEQVQIW2PcvXv3fwYg6OzsBFEMjBgCLi4uYBXl5eUQFegCAIYVD/syLxKnAAAAAElFTkSuQmCC);
  background-size: 9px 9px;
  image-rendering: pixelated;
  opacity: .1;
}
.is--flipped .ColorPicker-color {
  margin-top: 0;
  margin-bottom: 10px;
  border-radius: 0 0 2px 2px;
  top: auto;
  bottom: 0;
}
.is--flipped .ColorPicker-color:before,
.is--flipped .ColorPicker-color:after {
  border-radius: 0 0 2px 2px;
}
.view--definition .ColorPicker-color {
  display: none;
}
.ColorPicker-color-text {
  font-family: 'Source Code Pro', monospace, serif;
  font-weight: 600;
  font-size: 16px;
  -webkit-font-smoothing: antialiased;
  color: #fff;
  margin: 0;
  text-align: center;
  text-shadow: 0 1px 55px #000000, 0 0 18px rgba(0, 0, 0, 0.36), 7px 0 30px rgba(0, 0, 0, 0.25), -7px 0 30px rgba(0, 0, 0, 0.21), 0 1px 1px rgba(0, 0, 0, 0.15), 0 1px 0 rgba(0, 0, 0, 0.03), 0 -1px 1px rgba(0, 0, 0, 0.04), 26px 1px 31px rgba(0, 0, 0, 0.43), -26px 1px 31px rgba(0, 0, 0, 0.47), 0 1px 0 rgba(0, 0, 0, 0.04), 0 -1px 0 rgba(0, 0, 0, 0.04), 0 2px 1px rgba(0, 0, 0, 0.06);
  position: absolute;
  top: 50%;
  right: 0;
  left: 0;
  transition: transform .2s;
  transform: translate(0, -50%);
  backface-visibility: hidden;
  transform-style: preserve-3d;
}
.ColorPicker-color:hover .ColorPicker-color-text {
  transition: transform .067s;
  transform: translate(0, -60%);
}
.is--flipped .ColorPicker-color:hover .ColorPicker-color-text {
  transform: translate(0, -40%);
}
.ColorPicker-color.is--returnVisible .ColorPicker-color-text {
  transform: translate(0, -95%);
}
.ColorPicker-color.is--returnVisible:hover .ColorPicker-color-text {
  transform: translate(0, -105%);
}
.is--flipped .ColorPicker-color.is--returnVisible .ColorPicker-color-text {
  transform: translate(0, -5%);
}
.is--flipped .ColorPicker-color.is--returnVisible:hover .ColorPicker-color-text {
  transform: translate(0, 5%);
}
.ColorPicker-return {
  background: red;
  width: 100%;
  height: 26px;
  cursor: pointer;
  margin-top: 100px;
  border-radius: 2px 2px 0 0;
  box-shadow: 0 -1px 0 rgba(0, 0, 0, 0.07);
  position: absolute;
  top: 0;
  bottom: auto;
  visibility: hidden;
  backface-visibility: hidden;
  transform-style: preserve-3d;
  transition: transform .1s, visibility 0 .1s;
  transform: translateZ(1px);
}
.ColorPicker-return:before,
.ColorPicker-return:after {
  content: '';
  border-radius: 2px 2px 0 0;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: -1;
  backface-visibility: hidden;
  transform: translateZ(-1px);
}
.ColorPicker-return:before {
  background: #fff;
  opacity: 1;
}
.ColorPicker-return:after {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAJElEQVQIW2PcvXv3fwYg6OzsBFEMjBgCLi4uYBXl5eUQFegCAIYVD/syLxKnAAAAAElFTkSuQmCC);
  background-size: 9px 9px;
  image-rendering: pixelated;
  opacity: .1;
}
.is--flipped .ColorPicker-return {
  margin-top: 0;
  margin-bottom: 100px;
  border-radius: 0 0 2px 2px;
  box-shadow: 0 1px 0 rgba(0, 0, 0, 0.07);
  top: auto;
  bottom: 0;
}
.is--flipped .ColorPicker-return:before,
.is--flipped .ColorPicker-return:after {
  border-radius: 0 0 2px 2px;
}
.ColorPicker-return.is--visible {
  transition: transform .2s;
  transform: translateY(-26px) translateZ(1px);
  visibility: visible;
}
.is--flipped .ColorPicker-return.is--visible {
  transform: translateY(26px) translateZ(1px);
}
.view--definition .ColorPicker-return {
  display: none;
}
.ColorPicker-return-text {
  font-family: 'Source Code Pro', monospace, serif;
  font-weight: 500;
  font-size: 12px;
  -webkit-font-smoothing: antialiased;
  color: #fff;
  margin: 0;
  text-align: center;
  text-shadow: 0 1px 55px #000000, 0 0 18px rgba(0, 0, 0, 0.36), 7px 0 30px rgba(0, 0, 0, 0.25), -7px 0 30px rgba(0, 0, 0, 0.21), 0 1px 1px rgba(0, 0, 0, 0.15), 0 1px 0 rgba(0, 0, 0, 0.03), 0 -1px 1px rgba(0, 0, 0, 0.04), 26px 1px 31px rgba(0, 0, 0, 0.43), -26px 1px 31px rgba(0, 0, 0, 0.47), 0 1px 0 rgba(0, 0, 0, 0.04), 0 -1px 0 rgba(0, 0, 0, 0.04), 0 2px 1px rgba(0, 0, 0, 0.06);
  position: absolute;
  top: 50%;
  right: 0;
  left: 0;
  transition: transform .2s;
  transform: translate(0, -50%);
  backface-visibility: hidden;
  transform-style: preserve-3d;
}
.ColorPicker-return:hover .ColorPicker-return-text {
  transition: transform .067s;
  transform: translate(0, -60%);
}
.is--flipped .ColorPicker-return:hover .ColorPicker-return-text {
  transform: translate(0, -40%);
}
.ColorPicker-definition {
  background: rgba(0, 0, 0, 0);
  width: 200px;
  height: 90px;
  cursor: pointer;
  margin-top: 10px;
  border-radius: 2px;
  position: relative;
  display: none;
  transition: background .1s;
}
.ColorPicker-definition:before,
.ColorPicker-definition:after {
  content: '';
  border-radius: 2px;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: -1;
}
.ColorPicker-definition:before {
  background: #fff;
  opacity: .92;
}
.ColorPicker-definition:after {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAJElEQVQIW2PcvXv3fwYg6OzsBFEMjBgCLi4uYBXl5eUQFegCAIYVD/syLxKnAAAAAElFTkSuQmCC);
  background-size: 9px 9px;
  image-rendering: pixelated;
  opacity: .1;
}
.is--flipped .ColorPicker-definition {
  margin-top: 0;
  margin-bottom: 10px;
}
.view--definition .ColorPicker-definition {
  display: block;
}
.ColorPicker-definition-definition {
  font-family: 'Source Code Pro', monospace, serif;
  font-weight: 600;
  font-size: 17px;
  -webkit-font-smoothing: antialiased;
  color: #fff;
  margin: 0;
  text-align: center;
  text-shadow: 0 1px 55px #000000, 0 0 18px rgba(0, 0, 0, 0.36), 7px 0 30px rgba(0, 0, 0, 0.25), -7px 0 30px rgba(0, 0, 0, 0.21), 0 1px 1px rgba(0, 0, 0, 0.15), 0 1px 0 rgba(0, 0, 0, 0.03), 0 -1px 1px rgba(0, 0, 0, 0.04), 26px 1px 31px rgba(0, 0, 0, 0.43), -26px 1px 31px rgba(0, 0, 0, 0.47), 0 1px 0 rgba(0, 0, 0, 0.04), 0 -1px 0 rgba(0, 0, 0, 0.04), 0 2px 1px rgba(0, 0, 0, 0.06);
  position: absolute;
  top: 40%;
  right: 0;
  left: 0;
  transition: transform .2s;
  transform: translate(0, -50%);
  backface-visibility: hidden;
  transform-style: preserve-3d;
}
.ColorPicker-definition:hover .ColorPicker-definition-definition {
  transition: transform .067s;
  transform: translate(0, -60%);
}
.ColorPicker-definition-variable {
  font-family: 'Source Code Pro', monospace, serif;
  font-weight: 500;
  font-size: 12px;
  -webkit-font-smoothing: antialiased;
  color: #fff;
  margin: 0;
  text-align: center;
  text-shadow: 0 1px 55px #000000, 0 0 18px rgba(0, 0, 0, 0.36), 7px 0 30px rgba(0, 0, 0, 0.25), -7px 0 30px rgba(0, 0, 0, 0.21), 0 1px 1px rgba(0, 0, 0, 0.15), 0 1px 0 rgba(0, 0, 0, 0.03), 0 -1px 1px rgba(0, 0, 0, 0.04), 26px 1px 31px rgba(0, 0, 0, 0.43), -26px 1px 31px rgba(0, 0, 0, 0.47), 0 1px 0 rgba(0, 0, 0, 0.04), 0 -1px 0 rgba(0, 0, 0, 0.04), 0 2px 1px rgba(0, 0, 0, 0.06);
  position: absolute;
  top: 75%;
  right: 0;
  left: 0;
  transition: transform .2s;
  transform: translate(0, -50%);
  backface-visibility: hidden;
  transform-style: preserve-3d;
}
.ColorPicker-body {
  background: #ebebeb;
  padding: 7px;
  border-radius: 0 0 1px 1px;
  box-shadow: 0 -1px 5px rgba(0, 0, 0, 0.14), 0 -1px 0 rgba(0, 0, 0, 0.09);
  position: relative;
  top: 100px;
  z-index: 30;
  display: flex;
  transform: translateZ(2px);
}
.ColorPicker-body > div {
  margin-left: 6px;
}
.ColorPicker-body > div:first-child {
  margin-left: 0;
}
.is--flipped .ColorPicker-body {
  border-radius: 1px 1px 0 0;
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.14), 0 1px 0 rgba(0, 0, 0, 0.09);
  top: auto;
}
.view--definition .ColorPicker-body {
  display: none;
}
.ColorPicker-body-hue {
  width: 20px;
  height: 200px;
  cursor: -webkit-grab;
  cursor: grab;
  border: 1px solid #f2f2f2;
  border-radius: 2px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.07);
  position: relative;
}
.ColorPicker-body-hue:active {
  cursor: -webkit-grabbing;
  cursor: grabbing;
}
.ColorPicker-body-hue:after {
  content: '';
  border-radius: 1px;
  box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.07), inset 0 0 18px rgba(0, 0, 0, 0.2);
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}
.ColorPicker-body-hue-canvas {
  width: 100%;
  height: 100%;
  border-radius: 1px;
}
.ColorPicker-body-hue-control {
  height: 5px;
  margin-top: -2px;
  border: 2px solid #fff;
  border-radius: 1px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.3), 0 1px 1px rgba(0, 0, 0, 0.2);
  position: absolute;
  top: 50%;
  right: 1px;
  left: 1px;
  z-index: 1;
}
.ColorPicker-body-alpha {
  width: 20px;
  height: 200px;
  cursor: -webkit-grab;
  cursor: grab;
  border: 1px solid #f2f2f2;
  border-radius: 2px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.07);
  position: relative;
}
.ColorPicker-body-alpha:active {
  cursor: -webkit-grabbing;
  cursor: grabbing;
}
.ColorPicker-body-alpha:before {
  content: '';
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAJElEQVQIW2PcvXv3fwYg6OzsBFEMjBgCLi4uYBXl5eUQFegCAIYVD/syLxKnAAAAAElFTkSuQmCC);
  background-size: 9px 9px;
  image-rendering: pixelated;
  border-radius: 1px;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
  opacity: .1;
}
.ColorPicker-body-alpha:after {
  content: '';
  border-radius: 2px;
  box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.07), inset 0 0 18px rgba(0, 0, 0, 0.2);
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 3;
}
.ColorPicker-body-alpha-canvas {
  width: 100%;
  height: 100%;
  border-radius: 1px;
  position: relative;
  z-index: 2;
}
.ColorPicker-body-alpha-control {
  height: 5px;
  margin-top: -2px;
  border: 2px solid #fff;
  border-radius: 1px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.3), 0 1px 1px rgba(0, 0, 0, 0.2);
  position: absolute;
  top: 50%;
  right: 1px;
  left: 1px;
  z-index: 4;
}
.ColorPicker-body-saturation {
  width: 200px;
  height: 200px;
  cursor: crosshair;
  border: 1px solid #f2f2f2;
  border-radius: 2px;
  box-shadow: 0 0 1px rgba(0, 0, 0, 0.07);
  position: relative;
}
.ColorPicker-body-saturation:after {
  content: '';
  border-radius: 1px;
  box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.07), inset 0 0 18px rgba(0, 0, 0, 0.2);
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}
.ColorPicker-body-saturation-canvas {
  width: 100%;
  height: 100%;
  border-radius: 1px;
}
.ColorPicker-body-saturation-control {
  width: 9px;
  height: 9px;
  margin-top: -5px;
  margin-left: -5px;
  border: 2px solid #fff;
  border-radius: 100%;
  box-shadow: inset 0 0 1px rgba(0, 0, 0, 0.3), 0 0 1px rgba(0, 0, 0, 0.3), 0 1px 1px rgba(0, 0, 0, 0.2);
  position: absolute;
  top: 50%;
  left: 50%;
  z-index: 1;
}
.ColorPicker-format {
  background: #c0c0c0;
  width: 100%;
  height: 23px;
  border-radius: 1px;
  position: absolute;
  top: auto;
  bottom: -22px;
  display: flex;
  backface-visibility: hidden;
}
.is--flipped .ColorPicker-format {
  top: -22px;
  bottom: auto;
}
.view--definition .ColorPicker-format {
  display: none;
}
.ColorPicker-format-button {
  background: #d6d6d6;
  width: 100%;
  height: 100%;
  font-size: 9px;
  color: #777;
  line-height: 22px;
  text-shadow: 0 1px #e7e7e7;
  margin-right: 1px;
  border: 0;
  border-radius: 1px;
  box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.3), inset 0 2px 0 #c3c3c3;
  -webkit-appearance: none;
}
.ColorPicker-format-button:last-child {
  margin-right: 0;
}
.ColorPicker-format-button:hover {
  background: #ddd;
}
.is--flipped .ColorPicker-format-button {
  box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.3), inset 0 -2px 0 #c3c3c3;
}
.ColorPicker-format-button.is--active {
  background: #ebebeb;
  color: #555;
  box-shadow: inset 0 1px 3px #fff;
  text-shadow: none;
}
.is--flipped .ColorPicker-format-button.is--active {
  box-shadow: inset 0 -1px 3px #ffffff;
}
