css:
.settings-view .package-update-view {
  border-radius: 1px;
  padding-left: 10px;
  padding-right: 10px;
}
.settings-view .package-update-view.col-md-6 {
  min-width: 190px;
  padding: 10px 0;
  padding-left: 10px;
  padding-right: 10px;
}
.settings-view .package-update-view .thumbnail {
  border-radius: 3px;
  margin: 0;
  min-height: 140px;
  color: #aaaaaa;
  border-radius: 4px;
  border: 1px solid rgba(0, 0, 0, 0.5);
  background-color: #303337;
}
.settings-view .package-update-view .thumbnail .caption {
  width: 100%;
  position: relative;
}
.settings-view .package-update-view .package-name {
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 100%;
  overflow: hidden;
  margin: 5px 0;
  color: #ffffff;
}
.settings-view .package-update-view .description {
  color: #aaaaaa;
  overflow: hidden;
  line-height: 1.5;
  min-height: 3em;
  max-height: 3em;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .package-update-view .downloads {
  color: #ffffff;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .package-update-view .upgrade-button.btn-progress {
  opacity: 1;
  background-image: linear-gradient(to right, transparent 50%, rgba(0, 0, 0, 0.15) 50%);
  background-size: 10px 100%;
  -webkit-animation: settings-package-updating 0.5s linear infinite;
}
.settings-view .package-update-view .upgrade-button.btn-success {
  opacity: 1;
}
.settings-view .package-update-view .package-status {
  position: absolute;
  right: 0px;
  top: 5px;
  text-align: center;
}
.settings-view .package-update-view .package-status.icon-cloud-download {
  color: #428bca;
}
.settings-view .package-update-view .package-status.icon-check {
  color: #6CC644;
}
.settings-view .package-update-view .package-status.icon-alert {
  color: #BD2C00;
}
@-webkit-keyframes settings-package-updating {
  100% {
    background-position: -10px 0px;
  }
}
