css:
.ColorPicker-color {
  width: 100%;
  height: 100px;
  cursor: pointer;
  text-align: center;
  border-radius: 2px 2px 0 0;
  position: absolute;
  top: 0;
}
.ColorPicker-color:before {
  content: '';
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAJElEQVQIW2PcvXv3fwYg6OzsBFEMjBgCLi4uYBXl5eUQFegCAIYVD/syLxKnAAAAAElFTkSuQmCC);
  background-size: 10px 10px;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  z-index: -1;
  opacity: .1;
}
.ColorPicker-color:after {
  content: '';
  width: 0;
  height: 0;
  margin: auto;
  border: 8px solid transparent;
  border-bottom-color: inherit;
  position: absolute;
  top: -16px;
  left: 0;
  right: 0;
}
.no--arrow .ColorPicker-color:after {
  display: none;
}
.is--pointer .ColorPicker-color {
  border-radius: 2px;
}
.is--searching .ColorPicker-color:after {
  display: none;
}
.ColorPicker-value,
.ColorPicker-initial {
  font-family: 'Source Code Pro', 'Lucida Grande', 'Segoe UI', sans-serif;
  color: #fff;
  text-align: center;
  text-shadow: 0 2px 30px rgba(0, 0, 0, 0.5), 0 0 2px rgba(0, 0, 0, 0.3), 0 1px 1px rgba(0, 0, 0, 0.1), 0 1px rgba(0, 0, 0, 0.1), 0 -1px 4px rgba(0, 0, 0, 0.1), 0 0 1px rgba(0, 0, 0, 0.3);
}
.ColorPicker-value {
  font-size: 17px;
  font-weight: 400;
  position: absolute;
  top: 50%;
  right: 0;
  left: 0;
  pointer-events: none;
  -webkit-transition: -webkit-transform 0.3s;
  -webkit-transform: translate(0, -105%);
  -webkit-backface-visibility: hidden;
  -webkit-transform-style: preserve-3d;
}
.is--color_hsla .ColorPicker-value {
  font-size: 15px;
}
.ColorPicker-color:hover .ColorPicker-value {
  -webkit-transform: translate(0, -140%);
}
.is--initial .ColorPicker-value {
  -webkit-transform: translate(0, -50%);
}
.is--initial .ColorPicker-color:hover .ColorPicker-value {
  -webkit-transform: translate(0, -90%);
}
.is--pointer .ColorPicker-value {
  -webkit-transform: translate(0, -90%);
}
.is--pointer .ColorPicker-color:hover .ColorPicker-value {
  -webkit-transform: translate(0, -95%) scale(1.1);
}
.is--error .ColorPicker-value,
.is--error .ColorPicker-color:hover .ColorPicker-value {
  -webkit-transform: translate(0, -50%);
}
.is--pointer .ColorPicker-value:after {
  content: attr(data-variable);
  font-size: 11px;
  color: #fff;
  text-overflow: ellipsis;
  text-shadow: 0 0 1px #000;
  white-space: nowrap;
  margin: auto;
  overflow: hidden;
  position: absolute;
  top: 30px;
  right: 5px;
  left: 5px;
}
.is--searching .ColorPicker-value {
  display: none;
}
.is--error .ColorPicker-value:after {
  content: 'No color found :(';
  font-size: 11px;
  color: #000;
  text-shadow: none;
  margin: auto;
  overflow: hidden;
  top: -5px;
  right: 5px;
  left: 5px;
}
.ColorPicker-initialWrapper {
  background: #fff;
  width: 100%;
  cursor: pointer;
  position: absolute;
  top: 70px;
  -webkit-backface-visibility: hidden;
  -webkit-transition: -webkit-transform 0.3s;
  -webkit-transform: translate3d(0, 0, 0);
}
.ColorPicker-initialWrapper:hover {
  -webkit-transform: translate(0, -20%);
}
.is--initial .ColorPicker-initialWrapper {
  pointer-events: none;
  -webkit-transform: translate(0, 100%);
}
.is--pointer .ColorPicker-initialWrapper {
  display: none;
}
.ColorPicker-initialWrapper:before {
  content: '';
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAJElEQVQIW2PcvXv3fwYg6OzsBFEMjBgCLi4uYBXl5eUQFegCAIYVD/syLxKnAAAAAElFTkSuQmCC);
  background-size: 10px 10px;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  z-index: -1;
  opacity: .1;
}
.ColorPicker-initial {
  font-weight: 300;
  padding: 6px 0 20px;
  pointer-events: none;
}
