css:
.ColorPicker {
  background: #fff;
  width: 260px;
  height: 300px;
  border-radius: 3px 3px 2px 2px;
  box-shadow: 0 10px 30px 10px rgba(0, 0, 0, 0.3);
  position: absolute;
  top: 50%;
  bottom: 0;
  z-index: 40;
  visibility: hidden;
  opacity: 0;
  pointer-events: none;
  -webkit-transform-origin: 50% 0;
  -webkit-transition: -webkit-transform 0.15s, opacity 0.15s, visibility 0 0.15s;
  -webkit-transform: scale(0.6);
  -webkit-backface-visibility: hidden;
  -webkit-transform-style: preserve-3d;
}
.ColorPicker.is--visible {
  visibility: visible;
  opacity: 1;
  pointer-events: all;
  -webkit-transition: -webkit-transform 0.2s, opacity 0.2s;
  -webkit-transform: scale(1);
}
.ColorPicker.is--pointer {
  height: 100px;
}
.ColorPicker-picker {
  background: #ebebeb;
  width: 100%;
  height: 200px;
  box-shadow: 0 -1px rgba(0, 0, 0, 0.2);
  position: absolute;
  bottom: 0;
  z-index: 1;
  border-radius: 0 0 3px 3px;
}
.is--pointer .ColorPicker-picker {
  display: none;
}
